mapper.update:
UPDATE schedule_info 
   SET S_NAME          = #{SC_NAME}
 WHERE SID = #{SID};

mapper.select:
SELECT COUNT(T.SID)
  FROM schedule_info T
 WHERE 1=1 

      AND T.${searchField1} = #{searchValue1}


   	  AND DATE_FORMAT(T.INPUT_DATE, '%Y%m%d') >= #{searchStartDateValue1}


	  AND DATE_FORMAT(T.INPUT_DATE, '%Y%m%d') <= #{searchEndDateValue1}


      AND T.${searchField2} LIKE CONCAT('%',#{searchValue2},'%')


      AND T.${searchField3} LIKE CONCAT('%',#{searchValue3},'%')

SELECT T.SID, T.SC_NAME, T.SC_SITE, T.SC_URL, T.SC_TYPE, T.SC_EXT, T.SC_MAXSIZE, CASE WHEN T.SC_STATE = 0 THEN '대기' WHEN T.SC_STATE = 1 THEN '진행' WHEN T.SC_STATE = 3 THEN '취소' ELSE '완료' END SC_STATE, T.SC_COUNT, CONCAT(T.SC_COUNT,CONCAT(' (', CONCAT(T.SC_DOWNLOAD, CONCAT('/', CONCAT(T.SC_COUNT_E, ')'))))) SC_COUNT_W, T.SC_COUNT_E, T.USER_ID, DATE_FORMAT(T.INPUT_DATE, '%Y-%m-%d') INPUT_DATE, DATE_FORMAT(T.END_DATE, '%Y-%m-%d') END_DATE, @ROWNUM := @ROWNUM + 1 AS ROWNUM FROM schedule_info T , (SELECT @ROWNUM := 0) R WHERE 1=1
AND T.${searchField1} = #{searchValue1}
AND DATE_FORMAT(T.INPUT_DATE, '%Y%m%d') >= #{searchStartDateValue1}
AND DATE_FORMAT(T.INPUT_DATE, '%Y%m%d') <= #{searchEndDateValue1}
AND T.${searchField2} LIKE CONCAT('%',#{searchValue2},'%')
AND T.${searchField3} LIKE CONCAT('%',#{searchValue3},'%')
ORDER BY T.INPUT_DATE DESC
LIMIT #{start}, #{pageSize}

mapper.delete:
UPDATE schedule_info 
   SET SC_STATE = 3
 WHERE SID = #{SID}
